/**
 * @param {number[][]} grid
 * @return {number}
 */
var minCost = function(grid) {
  const map = {};
  let set = [];
  let ans = Infinity;
  const rlength = grid.length;
  const clength = grid[0].length;
  const dfs = (cur, r, c) => {
    if ((r < 0) | (r >= rlength) | (c < 0) | (c >= clength)) {
      return;
    }
    if (r === rlength - 1 && c === clength - 1) {
      if (cur < ans) ans = cur;
    }
    if (map[r + '-' + c] >= 0) {
      return;
    }
    map[r + '-' + c] = cur;
    set.push([cur, r, c]);
    if (grid[r][c] === 1) {
      dfs(cur, r, c + 1);
    } else if (grid[r][c] === 2) {
      dfs(cur, r, c - 1);
    } else if (grid[r][c] === 3) {
      dfs(cur, r + 1, c);
    } else if (grid[r][c] === 4) {
      dfs(cur, r - 1, c);
    }
  };

  dfs(0, 0, 0);
  while (set.length > 0) {
    const newSet = [];
    const update = (cur, r, c) => {
      if (map[r + '-' + c] >= 0 && map[r + '-' + c] <= cur) {
        return;
      }
      map[r + '-' + c] = cur;
      newSet.push([cur, r, c]);
    };
    for (let i = 0; i < set.length; i++) {
      const [cur, r, c] = set[i];
      if (r === rlength - 1 && c === clength - 1) {
        if (cur < ans) ans = cur;
      }
      if ((r < 0) | (r >= rlength) | (c < 0) | (c >= clength)) {
        continue;
      }
      if (grid[r][c] === 1) {
        update(cur, r, c + 1);
        update(cur + 1, r + 1, c);
        update(cur + 1, r, c - 1);
        update(cur + 1, r - 1, c);
      } else if (grid[r][c] === 2) {
        update(cur, r, c - 1);
        update(cur + 1, r + 1, c);
        update(cur + 1, r, c + 1);
        update(cur + 1, r - 1, c);
      } else if (grid[r][c] === 3) {
        update(cur, r + 1, c);
        update(cur + 1, r, c + 1);
        update(cur + 1, r, c - 1);
        update(cur + 1, r - 1, c);
      } else if (grid[r][c] === 4) {
        update(cur, r - 1, c);
        update(cur + 1, r, c + 1);
        update(cur + 1, r, c - 1);
        update(cur + 1, r + 1, c);
      }
    }
    set = newSet;
  }
  return ans;
};

